import React, { useState } from 'react';

const Step1 = ({ nextStep, handleFormData, values }) => {
    const [error, setError] = useState('');

    const handleChange = (input) => (e) => {
        handleFormData({ [input]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (values.password !== values.confirmPassword) {
            setError('Passwords don\'t match!');
        } else {
            setError('');
            nextStep();
        }
    };

    return (
        <div className="w-full max-w-2xl mx-auto mt-10 bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-center text-purple-600 mb-4">SIGN UP YOUR USER ACCOUNT</h2>
            <p className="text-center text-gray-500 mb-4">Fill all form fields to go to the next step</p>

            {/* Progress bar and other contents go here */}

            <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
                        Email
                    </label>
                    <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="email" type="email" placeholder="Email" />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="username">
                        Username
                    </label>
                    <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="Username" />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
                        Password
                    </label>
                    <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="password" type="password" placeholder="Password" />
                    <p className="text-red-500 text-xs italic mt-2">Please choose a password.</p>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="confirmPassword">
                        Confirm Password
                    </label>
                    <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="confirmPassword" type="password" placeholder="Confirm Password" />
                </div>
                <div className="flex items-center justify-between">
                    <button onClick={handleSubmit} className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                        Next
                    </button>
                </div>
            </div>
        </div>

    );
};

export default Step1;
