import React from 'react';

const Step2 = ({ nextStep, prevStep, handleFormData, values }) => {

    const handleChange = (e) => {
        handleFormData({ [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        nextStep();
    };

    return (
        <div className="w-full max-w-2xl mx-auto mt-10 bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-center text-purple-600 mb-4">Personal Information</h2>

            <form onSubmit={handleSubmit}>
                <div className="mb-6">
                    <label htmlFor="fname" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">First Name</label>
                    <input
                        type="text"
                        id="fname"
                        name="firstName"
                        value={values.firstName}
                        onChange={handleChange}
                        className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    />
                </div>
                <div className="mb-6">
                    <label htmlFor="lname" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Last Name</label>
                    <input
                        type="text"
                        id="lname"
                        name="lastName"
                        value={values.lastName}
                        onChange={handleChange}
                        className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    />
                </div>
                <div className="mb-6">
                    <label htmlFor="contactNo" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Contact Number</label>
                    <input
                        type="text"
                        id="contactNo"
                        name="contactNo"
                        value={values.contactNo}
                        onChange={handleChange}
                        className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    />
                </div>
                <div>
                    <label htmlFor="altContactNo" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Alternative Contact Number</label>
                    <input
                        type="text"
                        id="altContactNo"
                        name="altContactNo"
                        value={values.altContactNo}
                        onChange={handleChange}
                        className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    />
                </div>
                <div className="buttons-container">
                    <button
                        className="previous-button"
                        type="button"
                        onClick={prevStep}
                    >
                        Previous
                    </button>
                    <button
                        className="next-button"
                        type="button"
                        onClick={nextStep}
                    >
                        Next
                    </button>
                </div>


            </form>
        </div>
    );
};

export default Step2;
