import React, { useState } from 'react';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';
import Step4 from './Step4';
import Stepper from './stepper';

const SignupForm = () => {
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        email: '', username: '', password: '', confirmPassword: '',
        firstName: '', lastName: '', contactNo: '',
        photo: null
    });

    const nextStep = () => setCurrentStep((prev) => Math.min(prev + 1, 4));
    const prevStep = () => setCurrentStep((prev) => Math.max(prev - 1, 1));
    const handleFormData = (data) => setFormData({ ...formData, ...data });

    return (
        <div className="max-w-xl mx-auto mt-10 bg-white p-8 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold text-center text-purple-600 mb-6">SIGN UP YOUR USER ACCOUNT</h2>
            <p className="text-center text-gray-500 mb-6">Fill all form fields to proceed to the next step</p>

            <Stepper currentStep={currentStep} />

            <div className="mt-8">
                {currentStep === 1 && <Step1 nextStep={nextStep} handleFormData={handleFormData} values={formData} />}
                {currentStep === 2 && <Step2 nextStep={nextStep} prevStep={prevStep} handleFormData={handleFormData} values={formData} />}
                {currentStep === 3 && <Step3 nextStep={nextStep} prevStep={prevStep} handleFormData={handleFormData} values={formData} />}
                {currentStep === 4 && <Step4 prevStep={prevStep} />}
            </div>

            <div className="flex justify-between mt-8">
                {currentStep > 1 && <button onClick={prevStep} className="bg-gray-200 text-gray-700 px-4 py-2 rounded">Previous</button>}
                {currentStep < 4 && <button onClick={nextStep} className="bg-purple-600 text-white px-4 py-2 rounded">Next</button>}
            </div>
        </div>
    );
};

export default SignupForm;
