import React, { useState } from 'react';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';
import Step4 from './Step4';

const SignupForm = () => {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        email: '', username: '', password: '', confirmPassword: '',
        firstName: '', lastName: '', contactNo: '',
        photo: null
    });

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);
    const handleFormData = (data) => setFormData({ ...formData, ...data });

    switch (step) {
        case 1: return <Step1 nextStep={nextStep} handleFormData={handleFormData} values={formData} />;
        case 2: return <Step2 nextStep={nextStep} prevStep={prevStep} handleFormData={handleFormData} values={formData} />;
        case 3: return <Step3 nextStep={nextStep} prevStep={prevStep} handleFormData={handleFormData} values={formData} />;
        case 4: return <Step4 prevStep={prevStep} />;
        default: return <Step1 nextStep={nextStep} handleFormData={handleFormData} values={formData} />;
    }
};

export default SignupForm;
