import React from 'react';
import { useForm } from 'react-hook-form';

const Step1 = ({ nextStep, handleFormData, values }) => {
    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = data => {
        handleFormData(data);
        nextStep();
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <h2>Account Information</h2>
            <div>
                <label>Email:</label>
                <input {...register("email", { required: true })} defaultValue={values.email} />
                {errors.email && <span>This field is required</span>}
            </div>
            <div>
                <label>Username:</label>
                <input {...register("username", { required: true })} defaultValue={values.username} />
                {errors.username && <span>This field is required</span>}
            </div>
            <div>
                <label>Password:</label>
                <input type="password" {...register("password", { required: true })} defaultValue={values.password} />
                {errors.password && <span>This field is required</span>}
            </div>
            <div>
                <label>Confirm Password:</label>
                <input type="password" {...register("confirmPassword", { required: true })} defaultValue={values.confirmPassword} />
                {errors.confirmPassword && <span>This field is required</span>}
            </div>
            <button type="submit">Next</button>
        </form>
    );
};

export default Step1;
