import React from 'react';
import { useForm } from 'react-hook-form';

const Step2 = ({ nextStep, prevStep, handleFormData, values }) => {
    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = data => {
        handleFormData(data);
        nextStep();
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <h2>Personal Information</h2>
            <div>
                <label>First Name:</label>
                <input {...register("firstName", { required: true })} defaultValue={values.firstName} />
                {errors.firstName && <span>This field is required</span>}
            </div>
            <div>
                <label>Last Name:</label>
                <input {...register("lastName", { required: true })} defaultValue={values.lastName} />
                {errors.lastName && <span>This field is required</span>}
            </div>
            <div>
                <label>Contact No.:</label>
                <input {...register("contactNo", { required: true })} defaultValue={values.contactNo} />
                {errors.contactNo && <span>This field is required</span>}
            </div>
            <button type="button" onClick={prevStep}>Previous</button>
            <button type="submit">Next</button>
        </form>
    );
};

export default Step2;
