import React from 'react';
import { useForm } from 'react-hook-form';

const Step1 = ({ nextStep, handleFormData, values }) => {
    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = data => {
        handleFormData(data);
        nextStep();
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            <h3 className="text-lg font-bold text-purple-600 mb-4">Account Information:</h3>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">Email:</label>
                <input className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-100 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-purple-600" {...register("email", { required: true })} defaultValue={values.email} />
                {errors.email && <span className="text-red-500 text-xs italic">This field is required</span>}
            </div>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">Username:</label>
                <input className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-100 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-purple-600" {...register("username", { required: true })} defaultValue={values.username} />
                {errors.username && <span className="text-red-500 text-xs italic">This field is required</span>}
            </div>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">Password:</label>
                <input type="password" className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-100 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-purple-600" {...register("password", { required: true })} defaultValue={values.password} />
                {errors.password && <span className="text-red-500 text-xs italic">This field is required</span>}
            </div>
            <div className="mb-6">
                <label className="block text-gray-700 text-sm font-bold mb-2">Confirm Password:</label>
                <input type="password" className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-100 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-purple-600" {...register("confirmPassword", { required: true })} defaultValue={values.confirmPassword} />
                {errors.confirmPassword && <span className="text-red-500 text-xs italic">This field is required</span>}
            </div>
            <button type="submit" className="bg-purple-600 text-white font-bold py-2 px-4 rounded w-full focus:outline-none focus:ring-2 focus:ring-purple-600 hover:bg-purple-700 transition duration-300">Next</button>
        </form>
    );
};

export default Step1;
