import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDropzone } from 'react-dropzone';

const Step3 = ({ nextStep, prevStep, handleFormData, values }) => {
    const [preview, setPreview] = useState(values.photo || null);
    const { register, handleSubmit, formState: { errors } } = useForm();
    const { getRootProps, getInputProps } = useDropzone({
        accept: 'image/*',
        onDrop: acceptedFiles => {
            const file = acceptedFiles[0];
            setPreview(URL.createObjectURL(file));
            handleFormData({ photo: file });
        }
    });

    const onSubmit = data => {
        handleFormData(data);
        nextStep();
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <h2>Image Upload</h2>
            <div {...getRootProps()} style={{ border: '2px dashed gray', padding: '20px', textAlign: 'center' }}>
                <input {...getInputProps()} />
                {preview ? <img src={preview} alt="Preview" style={{ maxHeight: '200px' }} /> : <p>Drag & drop a file or click to select</p>}
            </div>
            <button type="button" onClick={prevStep}>Previous</button>
            <button type="submit">Submit</button>
        </form>
    );
};

export default Step3;
