import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock, faUser, faCamera, faCheck } from '@fortawesome/free-solid-svg-icons';

const Stepper = ({ currentStep }) => {
    const steps = [
        { id: 1, icon: faLock, label: 'Account' },
        { id: 2, icon: faUser, label: 'Personal' },
        { id: 3, icon: faCamera, label: 'Image' },
        { id: 4, icon: faCheck, label: 'Finish' },
    ];

    return (
        <div className="flex justify-between items-center mb-8">
            {steps.map((step, index) => (
                <React.Fragment key={step.id}>
                    <div className="flex flex-col items-center">
                        <div className={`w-12 h-12 rounded-full flex items-center justify-center 
            ${currentStep >= step.id ? 'bg-purple-600 text-white' : 'bg-gray-300 text-gray-600'}`}>
                            <FontAwesomeIcon icon={step.icon} />
                        </div>
                        <span className={`mt-2 ${currentStep >= step.id ? 'text-purple-600 font-bold' : 'text-gray-600'}`}>
                            {step.label}
                        </span>
                    </div>
                    {index < steps.length - 1 && (
                        <div className={`flex-1 border-t-4 mx-4 ${currentStep > step.id ? 'border-purple-600' : 'border-gray-300'}`} />
                    )}
                </React.Fragment>
            ))}
        </div>
    );
};

export default Stepper;
